changeSet.sql:
CREATE TABLE public.mapping
(
    system_id   INTEGER       NOT NULL UNIQUE,
    id          VARCHAR(30)   NOT NULL,
    first       VARCHAR(30)   NOT NULL,
    second      VARCHAR(30)   NOT NULL
);
COMMENT ON COLUMN public.mapping.system_id  IS 'Id системы';
COMMENT ON COLUMN public.mapping.id         IS 'Наименование поля id';
COMMENT ON COLUMN public.mapping.first      IS 'Наименование поля first';
COMMENT ON COLUMN public.mapping.second     IS 'Наименование поля second';
